INSERT INTO `email_templates` (`id`, `temp_id`, `subject`, `body`, `link_text`, `lang`, `type`, `lang_id`, `created_at`, `updated_at`) VALUES
(NULL, '12', 'Booking Confirmation for {property_name}', 'Hi {user_first_name},<div><p>\r\n\r\n<b>We are delighted to confirm your booking at {property_name}. Below are the details of your reservation:</b></p><div><ul><li>\r\n                            <b>Property Name:</b>  {property_name}\r\n                            </li><li>\r\n                            <b>Number of Guest:</b> {total_guest}\r\n                            </li><li>\r\n                            <b>Number of Night:</b> {total_night}\r\n                            </li><li>\r\n                            <b>Check in Time:</b> {start_date}\r\n                            </li><li><b>Total Amount:</b> {total_amount}</li></ul></div></div><div><br></div><div>\r\n\r\n-----------&nbsp;</div><div><br></div><div>If you have any questions or need further assistance, please do not hesitate to contact us.&nbsp;</div><div>We look forward to hosting you at \r\n\r\n{property_name}\r\n\r\n.&nbsp;</div><div><br></div><div><b>Best regards,&nbsp;</b></div><div><b>{company_name}</b><br></div>', NULL, 'en', 'email', '1', NULL, '2024-05-22 08:22:15'), 
(NULL, '13', 'Property Booked by {user_first_name}', 'Hi {owner_first_name},<br><br>\r\n                            <h3>\r\n\r\n<b>We are excited to inform you that your property - {property_name}, has been successfully booked by {user_first_name}. Below are the details of the reservation:</b></h3>\r\n                            \r\n\r\n<div><p><b>Booking Details:</b></p><div><ul><li><b>Property Name:</b>  {property_name}\r\n                            </li><li>\r\n                            <b>Number of Guest:</b> {total_guest}\r\n                            </li><li>\r\n                            <b>Number of Night:</b> {total_night}\r\n                            </li><li>\r\n                            <b>Check in Time:</b> {start_date}\r\n                            </li><li><b>Total Amount:</b> {total_amount}</li></ul></div></div><div><br></div><div><b>Guest Details:</b></div><div><ul><li><b>Name:</b> {user_name}\r\n\r\n</li><li><b>Email:</b> {user_email}</li></ul></div><div><br></div><div>----------</div><div>\r\n\r\n<p>Please ensure that the property is prepared for the guest\'s arrival on the specified dates. We recommend providing them with any necessary check-in instructions or additional details.</p><p>If you have any questions or need further assistance, feel free to contact us.</p><p>Thank you for using our platform to host guests. We appreciate your cooperation and wish you a pleasant experience with your guest.</p><p>\r\n\r\n</p><p><b>Best regards,</b></p><p><b>{company_name}</b></p><p></p></div>', NULL, 'en', 'email', '1', NULL, '2024-05-22 08:23:10');
(NULL, '14', 'Payment has been made for {property_name}', 'Hi {admin_first_name},\r\n                            <br><br>\r\n                            <h3>We wanted to inform you that a payment has been successfully processed for a property booking. Below are the details:\r\n\r\n</h3><ul><li>\r\n                            <b>Guest Name: </b>{user_name}</li><li>\r\n                            <b>Property Booked:</b>  {property_name}</li><li><b>Payment method:</b> {payment_method}</li><li><b>Payment Amount:</b> {total_amount}</li></ul><div><br>{bankMessages}</div><div><br></div><div>--------------------</div><div>\r\n\r\n<p>If you require any further information or assistance, please don\'t hesitate to reach out.</p><p>Thank you for your attention to this matter.</p><p>\r\n\r\n<b>Best regards,\r\n</b><br><b>{company_name}</b><br></p>\r\n\r\n<br></div>', NULL, 'en', 'email', '1', NULL, '2024-05-22 08:23:42'), 
(NULL, '15', 'New Payout Request Notification', 'Hi {admin_first_name},\r\n                            <br><br>\r\n                            <h3>\r\n\r\nWe are writing to inform you that a new payout request has been made. Below are the details of the request:</h3>\r\n                            \r\n\r\n<ul><li><strong>Requestor\'s Name</strong>: {user_name}</li><li><b>Payout Via</b>: {payout_method}</li><li><strong>Requestor\'s Email</strong>: {user_email}</li><li><strong>Amount Requested</strong>: {requested_amount}</li><li><strong>Date of Request</strong>: {requested_date}</li></ul>------------<div>\r\n\r\n<p>Please review and process this payout request at your earliest convenience. If you have any questions or require further information, do not hesitate to contact us.</p><p>Thank you for your attention to this matter.</p><p><b>Best regards,</b></p><p><b>{company_name}</b></p>\r\n\r\n<br></div>', NULL, 'en', 'email', '1', NULL, '2024-05-22 06:00:22'), 
(NULL, '16', 'Approval Request for Newly Listed Property - {property_name}', 'Hi {admin_first_name},\r\n                            <br><br>\r\n                            <h3>\r\n\r\nWe writing to inform you that {host_name} have listed a new property on our platform. Kindly review and approve the listing at your earliest convenience. Below are the details of the property:</h3>\r\n                            \r\n\r\n<ul><li>\r\n                            <b>Property Name:</b>  {property_name}\r\n                            </li><li><strong>Property Address</strong>: {property_address}</li><li><strong>Date Listed</strong>: {listed_date}</li></ul>\r\n\r\n\r\n                            --------------------<div>\r\n\r\n<p>Please let us know if there are any further details or modifications needed.</p><p>Thank you for your prompt attention to this matter.</p><p><b>Best regards,</b><br><b>{company_name}</b></p>\r\n\r\n<br></div>', NULL, 'en', 'email', '1', NULL, '2024-05-22 04:28:11'), 
(NULL, '17', 'Payout Request Approved and Processed', 'Hi {user_name},<br>\r\n                            <h1></h1>\r\n\r\n<h3>We are pleased to inform you that your payout request has been accepted. The requested amount has been sent to your selected payment method. Below are the details of your payout:</h3><ul><li><strong>Amount</strong>: {total_amount}</li><li><strong>Payment Method</strong>: {payment_method}</li><li><strong>Date Processed</strong>: {accepted_date}</li></ul>----------<br><p>If you have any questions or need further assistance, please do not hesitate to contact us.</p><p>Thank you for using our services.</p><p><b>Best regards,</b></p><p><b>{company_name}</b></p>', NULL, 'en', 'email', '1', NULL, '2024-05-22 07:08:54');
INSERT INTO `gateways` (`id`, `alias`, `name`, `sandbox`, `data`, `instruction`, `image`, `status`, `created_at`, `updated_at`) VALUES (NULL, 'razorpay', 'Razorpay', '1', '{"apiKey":"rzp_test","apiSecret":"rzp_test","instruction":"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.","status":"1","sandbox":"1"}', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'thumbnail.png', '1', NULL, NULL)